; This program is designed to be loaded into memory, and catch stray jumps; When a jump is caught, it will be reported to the operator; Equatesbdos	equ	0x0005fcb	equ	0x005Cdirent	equ	0x0080b_coin	equ	0x01b_cout	equ	0x02b_print	equ	0x09b_open	equ	0x0Fb_close	equ	0x10b_sfirs	equ	0x11b_snext	equ	0x12b_read	equ	0x14b_write	equ	0x15b_make	equ	0x16b_dma	equ	0x1A; Program start	org	0x0100	start:	ld	c,b_print	ld	de,splash	call	bdos		; Dump out all matching filenames into the heap	ld	c,b_dma	ld	de,dirent	call	bdos		ld	c,b_sfirs	ld	de,fcb	call	bdos		; If done, be donenextf:	ld	b,0xFF	cp	b	jr	z,endlook		; Calculate address of directory entry	ld	hl,dirent	ld	de,32gethl:	or	a	jr	z,gothl	dec	a	add	hl,de	jr	gethl	; Now copy it to heapgothl:	inc	hl	ld	de,(pointer)	ld	bc,11	ldir	ld	(pointer),de		; Find next filename	ld	c,b_snext	ld	de,fcb	call	bdos	jr	nextf	endlook:ld	de,(pointer)	xor	a	ld	(de),a	ld	hl,heap	ld	(pointer),hl			retsplash:		defb	'ISHKUR/NFS TextFix Utiltiy$'buffer:	defs	128pointer:	defw	heapheap: